        /// </summary>
        [TestMethod]
        public void TestMethod1()
        {
            string param1 = "abba";
            var act1 = new question5().isPalindromic(param1);

            string param2 = "aba";
            var act2 = new question5().isPalindromic(param2);

            string param3 = "abca";
            var act3 = new question5().isPalindromic(param3);

            string param4 = "a";
            var act4 = new question5().isPalindromic(param4);

            Assert.IsTrue(act1);
            Assert.IsTrue(act2);
            Assert.IsFalse(act3);
            Assert.IsTrue(act4);

        }

